

import os
import typing
import collections

from .StackTraceStruct import StackTraceStruct





class StackTraceStructList(list):

	################################################################################################################################
	## Constants
	################################################################################################################################

	################################################################################################################################
	## Constructor
	################################################################################################################################

	################################################################################################################################
	## Properties
	################################################################################################################################

	################################################################################################################################
	## Helper Methods
	################################################################################################################################

	################################################################################################################################
	## Public Methods
	################################################################################################################################

	def toJSONPretty(self) -> dict:
		ret = []
		for x in self:
			assert isinstance(x, StackTraceStruct)
			ret.append(x.toJSONPretty())
		return ret
	#

	def toJSON(self) -> list:
		ret = []
		for x in self:
			assert isinstance(x, StackTraceStruct)
			ret.append(x.toJSON())
		return ret
	#

	################################################################################################################################
	## Static Methods
	################################################################################################################################

	@staticmethod
	def fromJSONAny(data:typing.Union[list,tuple]):
		assert isinstance(data, (list, tuple))

		return [
			StackTraceStruct.fromJSONAny(x) for x in data
		]
	#

#




