


import jk_logging




class TestContext(object):

	################################################################################################################################
	## Constructor
	################################################################################################################################

	#
	# Constructor method.
	#
	def __init__(self, data:dict, globalVars:dict, log:jk_logging.AbstractLogger):
		assert isinstance(data, dict)
		assert isinstance(globalVars, dict)
		assert isinstance(log, jk_logging.AbstractLogger)

		self.__log = log
		self.__data = data
		self.__vars = globalVars
	#

	################################################################################################################################
	## Public Properties
	################################################################################################################################

	@property
	def data(self) -> dict:
		return self.__data
	#

	@property
	def vars(self) -> dict:
		return self.__vars
	#

	@property
	def log(self) -> jk_logging.AbstractLogger:
		return self.__log
	#

	################################################################################################################################
	## Helper Methods
	################################################################################################################################

	################################################################################################################################
	## Public Methods
	################################################################################################################################

#



